import React, { useEffect } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faBars } from '@fortawesome/free-solid-svg-icons'
import { getStorage, ref, getDownloadURL, getBlob } from 'firebase/storage'

function MyMusic(props) {

  const storage = getStorage();

  useEffect(() => {
    getDownloadURL(ref(storage, 'DUCKWORTH.mp3'))
    .then((url) => {
      const xhr = XMLHttpRequest();
      xhr.responseType = 'blob';
      xhr.onload = (event) => {
        const song = xhr.response;
      }
      xhr.open('GET', url);
      xhr.send()

      document.querySelector('.player').setAttribute('src', url)
    })
    .catch((error) => {
      console.log(error);
    })
  });
  return (
    <div className='home'>
      <h3 id="h3music">My Music</h3>
      <FontAwesomeIcon className='hamburger floatingHamburger' icon={faBars} onClick={props.toggleFun} />
    </div>
  )
}

export default MyMusic
