import React, { useEffect } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faBars } from '@fortawesome/free-solid-svg-icons'
import { getStorage, ref, getDownloadURL, getBlob } from 'firebase/storage'
import { doc, getDoc } from "firebase/firestore";
import { db } from '../FireBase'

function MyMusic(props) {

  getDoc(doc(db, 'Songs', 'test')).then(docSnap => {
    if (docSnap.exists()) {
      getDownloadURL(docSnap.data().test).then((url) => {
        const xhr = new XMLHttpRequest();
        xhr.responseType = 'blob';
        xhr.onload = (event) => {
          const song = xhr.response;
        }
        xhr.open('GET', url);
        xhr.send()

        document.querySelector('.audioplayer').setAttribute('src', url)
      })
    } else {
      console.log("Idiot")
    }
  })
  const docRef = doc(db, 'Songs', 'eygXJayODG5bRf5o0RyQ')
  const docSnap = getDoc(docRef)

  if(docSnap) {
    console.log(docSnap.data());
  } else {
    console.log('no such document')
  }


